import React, { useRef } from "react"
import { useBudgets, UNCATEGORISED_BUDGET_ID } from "../context/BudgetProvider";
import { Modal, Form, Button, FormGroup, FormLabel, FormControl, FormSelect } from 'react-bootstrap'
import FormRange from "react-bootstrap/esm/FormRange";


export default function AddExpenseModal({ show, handleClose, defaultBudgetId}) {
    const descriptionRef = useRef()
    const amountRef = useRef()
    const budgetIDRef = useRef()
    const { AddExpenseModal, budgets} = useBudgets()

    function handleSubmit(e) {
        e.preventDefault()
        AddExpenseModal({
            descripton: descriptionRef.current.value,
            amount: amountRef.current.value,
            budgetID: budgetIDRef.current.value
        })
        handleClose()

    }
    return (
        <Modal show={show} onHide={handleClose}>
            <Form onSubmit={handleSubmit}>
            <Modal.Header closeButton>
                <Modal.Title>New Expense</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form.Group controlID='description' className='mb-3'>
                        <FormLabel>Description</FormLabel>
                        <FormControl ref={ descriptionRef } type='text' required />
                    </Form.Group>
                    <FormGroup controlID='amount' className="mb-3">
                        <FormLabel>Amount</FormLabel>
                        <FormControl ref={amountRef} required type='number' min={0} step={.01} />
                    </FormGroup>
                    <FormGroup controlId="budgetId" className="mb-2">
                        <FormLabel>Budget</FormLabel>
                        <FormSelect
                            defaultValue={ defaultBudgetId }
                            ref={ budgetIDRef}
                            >
                            <option id={UNCATEGORISED_BUDGET_ID}>{UNCATEGORISED_BUDGET_ID}</option>
                            {
                                budgets.map(budget => {
                                    <option key={budget.id} value={budget.id}>{budget.name}</option>
                                })
                            }
                        </FormSelect>
                    </FormGroup>
                    <div className='d-flex justify-content-end'>
                    <Button variant='primary' type='submit'>Add Expense</Button>
                    </div>
                </Modal.Body>
            </Form>
        </Modal>
    )
}
